		AND CodiceVano IN (SELECT CodiceVano FROM Vano V WHERE V.CodiceEdificio = codice_edificio)
ON DUPLICATE KEY UPDATE    
Scostamento = (Scostamento + RankPunti.Scostamento)/2;


